Ext.define('A.view.VideoComponent', {
    extend: 'A.view.ui.VideoComponent',
    alias: 'widget.videocomponent',
	
    initComponent: function() {
        var me = this;
        me.callParent(arguments);
		me.on('afterrender', me.myAfterRender, me);
		me.addEvents('frame', 'start', 'stop');
    },
	myAfterRender: function() {
		this.videoEl = this.el.dom;
		this.videoEl.controls = true;
		this.load('videos/foo.webm');
		//this.el.on('play', function()  { console.log('play'); }, this);
		this.videoEl.addEventListener('play', function()  { console.log('play'); }, false);
	},
	load: function(videoUrl, posterUrl) {
		if (this.rendered) {
			this.videoEl.src = videoUrl;	
			if (posterUrl) {
				this.videoEl.poster = posterUrl;
			}
		}
		this.videoUrl = videoUrl; // save
	},
	start: function() {
		
	},
	stop: function() {
	
	},
	
	
});